import { HandleCharacterList } from '../Handler';
import * as ByteBuffer from 'bytebuffer';
import { default as GamePacket } from '../../game/Packet';
import { default as Opcode } from '../../game/Opcode';
import { expect } from 'chai';
import 'mocha';

describe('Hello function', () => {
  it('should return hello world', () => {
    // tslint:disable-next-line:max-line-length
    const hex = '';
    const packet = new GamePacket(Opcode.SMSG_CHAR_ENUM, hex.length, false);
    packet.append(ByteBuffer.fromHex(hex));
    packet.reset();
    HandleCharacterList(packet);
  });
});
